INSERT INTO `jos_virtuemart_categories_en_gb` (`virtuemart_category_id`, `category_name`, `category_description`, `metadesc`, `metakey`, `customtitle`, `slug`) VALUES
(1, 'Flowers and Gifts', '', '', '', '', 'Flowers-and-Gifts'),
(2, 'chocolate', '<table border="0">\r\n<tbody>\r\n<tr>\r\n<td style="text-align: justify;">Chocolate often represents a very nice gift, because the magic of a well-made chocolate is very much associated with the feminine charm. We deliver <strong>Dark Chocolate made by \\"Kanha\\"</strong> the famous sweet shop of Jaipur. We design a assorted basket, full of home made and imported chocolate. We also deliver bunch of home made chocolate in designer bag \\"Potli\\" shape. These chocolate have delicious taste and a perfect gift to give you a real pleasure.</td>\r\n<td><img src="images/stories/showcase/Chocolate gifts giftjaipur.jpg" border="0" /><br /></td>\r\n</tr>\r\n</tbody>\r\n</table>', '', '', '', 'chocolate'),
(3, 'Home made chocolates', '', '', '', '', 'Home-made-chocolates'),
(4, 'Ferrero rocher', '<p><img src="images/stories/showcase/ferrero rocher chocolate giftjaipur.JPG" border="0" /></p>', '', '', '', 'Ferrero-rocher'),
(5, 'Cakes', '', '', '', '', 'Cakes'),
(6, 'Birthday cakes', '', '', '', '', 'Birthday-cakes'),
(7, 'Eggless Cakes', '<p>we care for your emotions and respect your religion, these Egg less cakes will give you a homely feeling</p>', '', '', '', 'Eggless-Cakes'),
(8, 'Chocolate cake', '', '', '', '', 'Chocolate-cake'),
(9, 'Animated Cakes', '<p>Specially for the small ones at your house</p>', '', '', '', 'Animated-Cakes'),
(10, 'Combo Package', '', '', '', '', 'Combo-Package'),
(11, 'Chocolade', '<p>Orders may also be placed in advance for: Theme based <strong>Birthday parties</strong> (moulded chocolates in the shape of elephants, fish, turtles, rabbits, teddy bears and cars also available) <strong>Baby Showers</strong> (packaging appropriate) <strong>Corporate Gifting Gifts for festivals</strong> ( Diwali, Christmas, Easter, Holi, Rakhi, Valentines day) Weddings Corporate events</p>', '', '', '', 'Chocolade'),
(12, 'Birthday Gifts', '<p>Make this special day more auspicious and memorable. Send exclusive birthday gifts to your dear and near ones from giftjaipur.com.</p>', '', '', '', 'Birthday-Gifts'),
(13, 'Mugs', '<p>A birthday celebration deserves a festive mug. Giftjaipur has special mugs that everyone will look forward to using. Each mug has a different motif and color and coordinates with the Happy Birthday dinner plate. Collect several so the celebrant can choose their favorite!</p>', '', '', '', 'Mugs'),
(14, 'Sweets', '', '', '', '', 'Sweets'),
(15, 'Wedding gifts', '', '', '', '', 'Wedding-gifts'),
(16, 'Gifts for Her', '', '', '', '', 'Gifts-for-Her'),
(17, 'Gifts for Him', '', '', '', '', 'Gifts-for-Him'),
(18, 'Anniversary Gifts', '', '', '', '', 'Anniversary-Gifts'),
(19, 'Sunday Special', '<p>Jaipur\\''s breakfast tradition is predominantly vegetarian and dazzling in its variety. The spice content is on the higher side, even by Indian standards. In this category of Sunday special, We have tried to refresh your old days, where all Jaipurites enjoy Samosa, Kachori, Mava Kachori, Dal Kachori, Jalebi, Khamman, with their family members. You are not in Jaipur but still you can send Sunday special snacks to your loved ones living in Jaipur and make their day a special day.  GiftJaipur only delivers product made in Desi ghee only.  Please note that, to send these snacks your order should be minimum of 300 Rs</p>', '', '', '', 'Sunday-Special'),
(20, 'Mothers ''s Day special', '', '', '', '', 'Mothers-s-Day-special'),
(21, 'NewBorn', '', '', '', '', 'NewBorn'),
(22, 'Kids Gift', '', '', '', '', 'Kids-Gift'),
(23, 'Cookies', 'Freshly baked, hand decorated and beautifully packaged, GiftJaipur can deliver anywhere in the Jaipur.', '', '', '', 'Cookies'),
(24, 'Dry Fruits', '', '', '', '', 'Dry-Fruits'),
(25, 'Rakhi', '<p><img src="images/stories/showcase/shop for rakhi.jpg" border="0" /></p>', '', '', '', 'Rakhi'),
(26, 'Gifts For Brother', '', '', '', '', 'Gifts-For-Brother'),
(27, 'Gifts for Sister', '', '', '', '', 'Gifts-for-Sister'),
(28, 'Janmashtami Gifts', '', '', '', '', 'Janmashtami-Gifts'),
(29, 'Teacher''s day special', '', '', '', '', 'Teachers-day');
